(268, 'Luka Trifunovic, Piet W. Brouwer', 'Higher-Order Bulk-Boundary Correspondence for Topological Crystalline Phases', 2019, 1, 'http://journals.aps.org/prx/abstract/10.1103/PhysRevX.9.011012', 'prx', '9', '011012', 'PhysRevX.9.011012', '@article{PhysRevX.9.011012,\n  title = {Higher-Order Bulk-Boundary Correspondence for Topological Crystalline Phases},\n  author = {Trifunovic, Luka and Brouwer, Piet W.},\n  journal = {Phys. Rev. X},\n  volume = {9},\n  issue = {1},\n  pages = {011012},\n  numpages = {33},\n  year = {2019},\n  month = {Jan},\n  publisher = {American Physical Society},\n  doi = {10.1103/PhysRevX.9.011012},\n  url = {https://link.aps.org/doi/10.1103/PhysRevX.9.011012}\n}\n'),
(269, 'Vatsal Dwivedi, Victor Chua', 'Phase Space Entanglement Spectrum', 2018, 6, 'https://orcid.org/0000-0002-3315-7667', 'J. Phys. A: Math. Theor.', '51', '315304', '', '@article{Dwivedi_2018,\r\n	doi = {10.1088/1751-8121/aacc4d},\r\n	url = {https://doi.org/10.1088%2F1751-8121%2Faacc4d},\r\n	year = 2018,\r\n	month = {jun},\r\n	publisher = {{IOP} Publishing},\r\n	volume = {51},\r\n	number = {31},\r\n	pages = {315304},\r\n	author = {Vatsal Dwivedi and Victor Chua},\r\n	title = {Phase space entanglement spectrum},\r\n	journal = {Journal of Physics A: Mathematical and Theoretical},\r\n	annote = {The study of quantum entanglement involves the choice of an entanglement cut, often corresponding to a partitioning of the system in position space. We generalize these partitions to a family of entanglement cuts corresponding to arbitrary directions in the classical phase space. For one dimensional free fermionic many-body wavefunctions, we analytically compute the full entanglement spectra for a family of entanglement cuts corresponding to a rotation in the classical phase space. These cuts continuously interpolate between the commonly studied position- and momentum-space cuts, as well as provide an implementation of the inversion symmetry smoothly connected to identity. The inversion symmetry of the wavefunctions is reflected in the chiral symmetry of the phase space entanglement spectra, and we use the associated chiral invariant to classify the inversion symmetric fermionic many-body wavefunctions.}\r\n}'),
(271, 'Ciarán Hickey, Simon Trebst', 'Emergence of a field-driven U(1) spin liquid in the Kitaev honeycomb model', 2019, 1, 'https://www.nature.com/articles/s41467-019-08459-9', 'Nature Communications', '10', '530', '', '@article{Hickey2019,\r\n	Abstract = {In the field of quantum magnetism, the exactly solvable Kitaev honeycomb model serves as a paradigm for the fractionalization of spin degrees of freedom and the formation of {\\$}{\\$}{\\{}{$\\backslash$}Bbb Z{\\}}{\\_}2{\\$}{\\$}Z2quantum spin liquids. An intense experimental search has led to the discovery of a number of spin-orbit entangled Mott insulators that realize its characteristic bond-directional interactions and, in the presence of magnetic fields, exhibit no indications of long-range order. Here, we map out the complete phase diagram of the Kitaev model in tilted magnetic fields and report the emergence of a distinct gapless quantum spin liquid at intermediate field strengths. Analyzing a number of static, dynamical, and finite temperature quantities using numerical exact diagonalization techniques, we find strong evidence that this phase exhibits gapless fermions coupled to a massless U(1) gauge field. We discuss its stability in the presence of perturbations that naturally arise in spin-orbit entangled candidate materials.},\r\n	Author = {Hickey, Ciar{\\\'a}n and Trebst, Simon},\r\n	Da = {2019/01/31},\r\n	Date-Added = {2019-01-31 11:23:26 +0100},\r\n	Date-Modified = {2019-01-31 11:23:26 +0100},\r\n	Doi = {10.1038/s41467-019-08459-9},\r\n	Id = {Hickey2019},\r\n	Isbn = {2041-1723},\r\n	Journal = {Nature Communications},\r\n	Number = {1},\r\n	Pages = {530},\r\n	Title = {Emergence of a field-driven U(1) spin liquid in the Kitaev honeycomb model},\r\n	Ty = {JOUR},\r\n	Url = {https://doi.org/10.1038/s41467-019-08459-9},\r\n	Volume = {10},\r\n	Year = {2019},\r\n	Bdsk-Url-1 = {https://doi.org/10.1038/s41467-019-08459-9}}\r\n'),
