(360, 'Flore K. Kunst, Vatsal Dwivedi', 'Non-Hermitian systems and topology: A transfer-matrix perspective', 2019, 6, 'http://journals.aps.org/prb/abstract/10.1103/PhysRevB.99.245116', 'prb', '99', '245116', 'PhysRevB.99.245116(E)', '@article{PhysRevB.99.245116,\n  title = {Non-Hermitian systems and topology: A transfer-matrix perspective},\n  author = {Kunst, Flore K. and Dwivedi, Vatsal},\n  journal = {Phys. Rev. B},\n  volume = {99},\n  issue = {24},\n  pages = {245116},\n  numpages = {21},\n  year = {2019},\n  month = {Jun},\n  publisher = {American Physical Society},\n  doi = {10.1103/PhysRevB.99.245116},\n  url = {https://link.aps.org/doi/10.1103/PhysRevB.99.245116}\n}\n'),
(361, 'Hanteng Wang, D. Bagrets, A. L. Chudnovskiy, A. Kamenev', 'On the replica structure of Sachdev-Ye-Kitaev model', 2019, 9, 'https://doi.org/10.1007/JHEP09(2019)057', 'Journal of High Energy Physics', '9', '57', '', '@Article{Wang2019,\r\nauthor=\"Wang, Hanteng\r\nand Bagrets, D.\r\nand Chudnovskiy, A. L.\r\nand Kamenev, A.\",\r\ntitle=\"On the replica structure of Sachdev-Ye-Kitaev model\",\r\njournal=\"Journal of High Energy Physics\",\r\nyear=\"2019\",\r\nmonth=\"Sep\",\r\nday=\"09\",\r\nvolume=\"2019\",\r\nnumber=\"9\",\r\npages=\"57\",\r\nabstract=\"We investigate existence of replica off-diagonal solutions in the field-theoretical description of Sachdev-Ye-Kitaev model. To this end we evaluate a set of local and non-local dynamic correlation functions in the long time limit. We argue that the structure of the soft-mode Schwarzian action is qualitatively different in replica-diagonal vs. replica-off-diagonal scenarios, leading to distinct long-time predictions for the correlation functions. We then evaluate the corresponding correlation functions numerically and compare the simulations with analytical predictions of replica-diagonal and replica-off-diagonal calculations. We conclude that all our numerical results are in a quantitative agreement with the theory based on the replica-diagonal saddle point plus Schwarzian and massive Gaussian fluctuations (the latter do contain replica off-diagonal components). This seems to exclude any contributions from replica-off-diagonal saddle points, at least on the time scales shorter than the inverse many-body level spacing.\",\r\nissn=\"1029-8479\",\r\ndoi=\"10.1007/JHEP09(2019)057\",\r\nurl=\"https://doi.org/10.1007/JHEP09(2019)057\"\r\n}'),
